INSERT [dbo].[SQL源] ([名称], [F_SQL], [F_SQL1], [F_SQL2], [F_SQL3], [F_SQL4], [F_SQL5]) VALUES (N'CGD', N'SELECT   A.F_DJH, A.F_DATE, A.F_FPH, A.F_BM, B.F_BMMC, A.F_VENDOR, C.F_VENDORMC, A.F_YWY, D.F_YWYMC, A.F_HW, 
                E.F_HWMC, A.F_JSFS, A.F_JS, A.F_JSH, A.F_SFZY, A.F_ZD, A.F_FPXZ, A.F_HZ
FROM      CGD AS A LEFT OUTER JOIN
                BM AS B ON A.F_BM = B.F_DM LEFT OUTER JOIN
                VENDOR AS C ON A.F_VENDOR = C.F_DM LEFT OUTER JOIN
                YWY AS D ON A.F_YWY = D.F_DM LEFT OUTER JOIN
                HW AS E ON A.F_HW = E.F_DM', N'SELECT   A.F_DJH, A.F_HC, B.F_TM, A.F_SPBH, B.F_SPMC, B.F_SPGG, B.F_SPCD, A.F_DW, A.F_SL, A.F_DJ, A.F_JE, A.F_SLV, 
                A.F_SE, A.F_HJ, A.F_PH, A.F_DQ, A.F_UC, A.F_UN, A.F_DJ1, A.F_DJ2, A.F_SLJC, A.F_SL2, A.F_DJ3, A.F_DJ4, 
                A.F_DJ5
FROM      CGDMX AS A LEFT OUTER JOIN
                SP AS B ON A.F_SPBH = B.F_SPBH
WHERE A.F_DJH=:F_DJH', NULL, NULL, NULL, NULL)
